package com.xjjlearning.java.util.concurrent.chapter8;import java.util.concurrent.CountDownLatch;import java.util.concurrent.TimeUnit;/** *  * @author tengfei.fangtf * @version $Id: CountDownLatchTest.java, v 0.1 2015-8-1 ����12:08:52 tengfei.fangtf Exp $ */public class CountDownLatchTest {    static CountDownLatch c = new CountDownLatch(2);    public static void main(String[] args) throws InterruptedException {        new Thread(() -> {            try {                TimeUnit.SECONDS.sleep(1);            } catch (InterruptedException e) {                e.printStackTrace();            }            System.out.println(1);            c.countDown();            System.out.println(2);            c.countDown();        }).start();        System.out.println("3");        c.await();        System.out.println("4");    }}